import axios from 'axios';
import { useEffect, useState } from 'react';
import { Contxt } from './Components/Context';
import Card from './Components/Card';
import Chart from './Components/Chart';
import './App.css'

function App() {
const [loading, setloading] = useState(true)  

  let deta=Contxt()
  console.log(deta.data)
  

 

  const get = async () => {
  setloading(true);
  try {
    // 1. Get Coordinates from Current Weather API (2.5 is still fine for this)
    // Added &units=metric here to keep consistency
    const weatherRes = await axios.get(
      `https://api.openweathermap.org/data/2.5/weather?q=${deta.city}&units=metric&appid=7131d76449271d162d8aef8feb19706e`
    );
    
    const { lat, lon } = weatherRes.data.coord;
    deta.setinfo(weatherRes.data);

    // 2. Get Detailed Forecast from One Call 3.0
    const oneCallRes = await axios.get(
      `https://api.openweathermap.org/data/3.0/onecall?lat=${lat}&lon=${lon}&exclude=hourly,minutely,alerts&units=metric&appid=7131d76449271d162d8aef8feb19706e`
    );

    console.log('OneCall Data:', oneCallRes.data);
    
    deta.setcr(oneCallRes.data.current);
    deta.set(oneCallRes.data.daily);

  } catch (error) {
    console.error("Error fetching weather data:", error.response?.data || error.message);
    // Optional: alert user or set an error state here
  } finally {
    setloading(false);
  }
};



  
  useEffect(() => { 
    get()
  }, [])




  if(loading) return <h1>loading</h1>


  return (
    <div  >
      <Card get={get}/>
      <Chart/>
    </div>
  );
}

export default App;
